document.addEventListener("DOMContentLoaded",(function(){const s=document.getElementById("hamburger-toggle"),e=document.getElementsByClassName("overlay")[0];s.addEventListener("click",()=>{s.classList.contains("hamburger--active")&&e.classList.contains("overlay--open")?(s.classList.remove("hamburger--active"),e.classList.remove("overlay--open"),document.body.classList.remove("scroll-disabled")):(s.classList.add("hamburger--active"),e.classList.add("overlay--open"),document.body.classList.add("scroll-disabled"))}),$(".articles__wrapper--slider").slick({mobileFirst:!0,infinite:!0,arrows:!0,prevArrow:'<button class="arrow-left"><img src="img/dist/sprite.svg#arrow-left" alt=""></button>',nextArrow:'<button class="arrow-right"><img src="img/dist/sprite.svg#arrow-right" alt=""></button>',responsive:[{breakpoint:1199,settings:"unslick"},{breakpoint:767,settings:{slidesToShow:2}},{breakpoint:279,settings:{slidesToShow:1}}]})}));